// Fibonacci sequence is a sequence of numbers where starting from the 3rd number, each number is generated by adding up the previous two numbers, given that the first two numbers are 1.
// Time complexity is O(2^N)
// 1, 1, 2, 3, 5, 8 ...
const fib = (num) => {
  if (num > 2) {
    return fib(num - 1) + fib(num - 2);
  } else if (num > 0 && num <= 2) {
    // both 1 and 2 returns 1
    return 1;
  } else {
    // In case of 0 or -ve
    return 0;
  }
};

console.log(`the 3rd element in fibonacci-sequence is: ${fib(3)}`);
console.log(`the 5th element in fibonacci-sequence is: ${fib(5)}`);
console.log(`the 0th element in fibonacci-sequence is: ${fib(0)}`);
console.log(`the 1st element in fibonacci-sequence is: ${fib(1)}`);
console.log(`the 2nd element in fibonacci-sequence is: ${fib(2)}`);
console.log(`the 10th element in fibonacci-sequence is: ${fib(10)}`);
console.log(`the 20th element in fibonacci-sequence is: ${fib(20)}`);
